import {Component, OnInit} from '@angular/core';
import {FormControl, FormGroup, Validators} from "@angular/forms";
import {HttpClient} from "@angular/common/http";
import {SpinnerService} from "../service/spinner-service";

interface Device {
  id: string;
  uuid: string;
  name: string;
  owner: string;
  type: string;
}

interface MediaType {
  id: string;
  name: string;
}

class ResponseMessage {
  output: string = '';
  error: string = '';
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  readonly ROOT_URL = 'http://localhost:8387';
  readonly mediaTypes: MediaType[] = [
    {name: 'Pictures', id: 'IMG'},
    {name: 'Videos', id: 'VID'}
  ];

  sourceDevices: Device[] = [];
  targetDevices: Device[] = [];

  successfulRequest: boolean | null = null;
  responseMessage = {};

  sourceDeviceControl = new FormControl<Device | null>(null, Validators.required);
  targetDeviceControl = new FormControl<Device | null>(null, Validators.required);
  mediaTypeControl = new FormControl<MediaType | null>(null, Validators.required);
  backupForm = new FormGroup({
    sourceDevice: this.sourceDeviceControl,
    targetDevice: this.targetDeviceControl,
    mediaType: this.mediaTypeControl
  });

  constructor(private http: HttpClient, private spinnerService: SpinnerService) {
  }

  ngOnInit() {
    this.initDevices();
  }

  initDevices(): void {
    this.http.get<Device[]>(this.ROOT_URL + '/devices').subscribe(res => {
      this.sourceDevices = res.filter(device => device.type === 'source');
      this.targetDevices = res.filter(device => device.type === 'target');
    });
  }

  onSubmit(): void {
    const body = {mediaType: this.backupForm.value.mediaType?.id, deviceId: this.backupForm.value.sourceDevice?.id};
    const options = {};

    this.http.post<ResponseMessage>(this.ROOT_URL + '/launchBackup', body, options)
      .subscribe({
        next: (data) => {
          console.log(data.output);
          this.successfulRequest = true;
          this.responseMessage = data.output;
        }, error: (error) => {
          console.log(error);
          this.successfulRequest = false;
          this.responseMessage = error.error.text;
        }
      });
  }

  showSpinner() {
    return this.spinnerService.visibility;
  }
}
<!-- Toolbar -->
<div class="toolbar" role="banner">
  <img
    width="40"
    alt="App Logo"
    src="data:image/svg+xml;base64,"
  />
  <span>Media Files Backup</span>
  <div class="spacer"></div>
</div>

<div class="content" role="main">
  <form (ngSubmit)="onSubmit()" [formGroup]="backupForm">
    <div class="card-container">
      <mat-form-field appearance="outline">
        <mat-label>Media Type</mat-label>
        <mat-select required formControlName="mediaType">
          <mat-option *ngFor="let mediaType of mediaTypes" [value]="mediaType">
            {{mediaType.name}}
          </mat-option>
        </mat-select>
        <mat-error *ngIf="mediaTypeControl.hasError('required')">Please choose a media type</mat-error>
      </mat-form-field>
    </div>
    <div class="card-container">
      <mat-form-field appearance="outline">
        <mat-label>Source Device</mat-label>
        <mat-select required formControlName="sourceDevice">
          <mat-option *ngFor="let sourceDevice of sourceDevices" [value]="sourceDevice">
            {{sourceDevice.name}}
          </mat-option>
        </mat-select>
        <mat-error *ngIf="sourceDeviceControl.hasError('required')">Please choose a Device</mat-error>
      </mat-form-field>
      <div class="source-target-arrow">
        <mat-icon>arrow_right_alt</mat-icon>
      </div>
      <mat-form-field appearance="outline">
        <mat-label>Target Device</mat-label>
        <mat-select required formControlName="targetDevice">
          <mat-option *ngFor="let targetDevice of targetDevices" [value]="targetDevice">
            {{targetDevice.name}}
          </mat-option>
        </mat-select>
        <mat-error *ngIf="sourceDeviceControl.hasError('required')">Please choose a Device</mat-error>
      </mat-form-field>
    </div>

    <div class="card-container">
      <mat-spinner [diameter]="20" *ngIf="showSpinner() | async"></mat-spinner>
      <button mat-raised-button type="submit" [disabled]="backupForm.invalid || backupForm.pending">
        Launch Backup
      </button>
    </div>
  </form>

  <mat-divider></mat-divider>
  <!-- Terminal -->
  <div class="terminal"
       [ngClass]="successfulRequest === true ? 'success' : successfulRequest === null ? '' : 'failure'">
    <pre [innerHTML]="responseMessage"></pre>
  </div>

  <!-- Footer -->
  <footer>
  </footer>
</div>

<router-outlet></router-outlet>
